import {useState, useEffect,useRef}from 'react'
import Chart from 'chart.js/auto';
import './Money.css'
import Skeleton, { SkeletonTheme } from 'react-loading-skeleton'



const MoneyFull = () => {
  const [data7Days,setData7Days ]= useState([]);
  const [data15Days,setData15Days ]= useState([]);
  const [data30Days,setData30Days ]= useState([]);
  const [color,setColor] = useState('#9dc3660d')
  const [colorBorder,setColorBorder] = useState('#9DC366')
  const [loading,setLoading] = useState(false)
  const [interval, setInterval] = useState(7)






  async function requestApi (days, set) {
    let  api = `https://economia.awesomeapi.com.br/json/daily/EUR-BRL/${days}`
    fetch(api).then(response => response.json()).then((result) => 
    {set(result) 
      console.log(result)
          setLoading(true)}
          )
          .catch((error)=>{
            setTimeout(() => {
                requestApi()
                console.log('Try Again')
            }, 5000);
        })
  }
  useEffect(()=>{
    requestApi(7, setData7Days)
    requestApi(15, setData15Days)
    requestApi(30, setData30Days)
  },[interval])




  const ctx7 = document.getElementById('moneyFullChar7');
  const ctx15 = document.getElementById('moneyFullChar15');
  const ctx30 = document.getElementById('moneyFullChar15');

    if(data7Days.length>0){
    let chartStatus = Chart.getChart("moneyFullChar7"); // <canvas> id
    if (chartStatus != undefined) {
      chartStatus.destroy();
    }

    if(interval ==7){

      const thiWeek = new Chart(ctx7, {
        type: 'line',
        data: {
            labels: ['6 Dias atrás', '5 Dias atrás', '4 Dias atrás', '3 Dias atrás', '2 Dias atrás', 'Ontem','Agora'],
            datasets: [{
                label: "EUR",
                display: true,
                data: [   data7Days[6].ask, data7Days[5].ask, data7Days[4].ask, data7Days[3].ask, data7Days[2].ask, data7Days[1].ask,data7Days[0].ask],
                pointRadius: 0,
                backgroundColor: [
                   `${color}`
                  ],
                borderColor: colorBorder,
                borderWidth: 4,
                tension: 0.4, 
            }]
        },
        options: {
            fill: true,
            options:{
              aspectRatio:2/1 //(width/height)
         },
            plugins: {
                labels: {
                    display: false,
                },
                legend: {
                    display: false,
                },
            },
            scales: {
                y: {
                    display: true,                     
                },
                x: {
                    display: true ,
                }

            }
        },
        responsive: true,
        maintainAspectRatio: false,
        scales: {
            yAxes: [{
                ticks: {
                    beginAtZero:true,
                    stepSize: 1
                }
            }]
        }
    });

    } 


  }



  useEffect(()=>{

    if(data7Days.length>0){

        if(data7Days[1].ask<data7Days[2].ask){
          setColor('#9dc3660d')
          setColorBorder('#9DC366')

        } else{
          setColor('#FA385F0a')
          setColorBorder('#FA3850')
        }

  }
  },[data7Days])








  return (
    <div className='moneyFull-component'>

      {loading == true? 
          <>

            <div className='moneyFullChar-Box'>
              <div className="moneyFullChar-interval-box">
                <p>Intervalo de Tempo</p>
                <select onChange={(e)=>{setInterval(e.target.value)}}>
                  <option value={7}>Está semana</option>
                  <option value={15}>Últimos 15 dias</option>
                  <option value={30}>Últimos 30 Dias</option>
                </select>
              </div>

               <canvas id="moneyFullChar7"></canvas>

            </div>
            </>
        :
        <SkeletonTheme baseColor="var(--8)" highlightColor="var(--11)">
            <Skeleton style={{width:'110px',height:'20px',margin:'10px'}}/>
            <Skeleton style={{width:'130px',height:'30px',margin:'0px 10px'}}/>
            <Skeleton style={{width:'310px',height:'80px',margin:'10px 0px 0px 0px',opacity:'0.4'}}/>

          </SkeletonTheme>

        }
    </div>
  )
}

export default MoneyFull
