import {useEffect, useState} from 'react'
import './toDolist.css'
import ToDoListTrello from '../../components/toDoList/ToDoListTrello'
import Api from '../../axios/Axios'
import AddToDoList from '../../components/toDoList/AddToDoList'
const ToDoList = () => {


  const [list,setList] = useState(undefined);
  const [open,setOpen] = useState([]);
  const [inProgress, setInProgress] = useState([]);
  const [finished, setFinished] = useState([]);
  const [loading, setLoading] = useState(false)



  const customConfig = {
    headers: {
    'Content-Type': 'application/json',
    "Authorization" : `Bearer ${sessionStorage.getItem('token')}`
    }
};


  async function loadToDoList(){
    console.log('carregoooou')
    await Api('/user/todolist' ,customConfig).then((data)=>{
      setList(data.data.data)
    })
  }
  useEffect(()=>{


    loadToDoList()
  },[])


  function refresh(){
    setLoading(false)
    setList(undefined)
    setFinished([])
    setInProgress([])
    setOpen([])
    loadToDoList()

  }

  useEffect(()=>{
    if(list!== undefined){

      list.map((item)=>{
        if(item.status == 1){
        setOpen(open => [...open, item])
        }else if(item.status == 2){
          setInProgress(inProgress => [...inProgress, item])

        }else if(item.status ==3){
          setFinished(finished => [...finished, item])

        }
      })
      setLoading(true)

    }
  },[list])






  return (



    <>  
        <div className='todolist-page'>
          <div className="todolist-header-page">
            <h1>Lista de Tarefas</h1>
            <button className='btn-add-new'>
              <svg xmlns="http://www.w3.org/2000/svg" width="17.371" height="17.371" viewBox="0 0 17.371 17.371">
                <g id="ic-actions-close" transform="matrix(0.719, -0.695, 0.695, 0.719, 2.5, 8.578)">
                  <line id="Linha_16" data-name="Linha 16" x1="8.749" y1="8.749" transform="translate(0 0)" fill="none" stroke="#fff" stroke-linecap="round" stroke-linejoin="bevel" stroke-width="2.5"/>
                  <line id="Linha_17" data-name="Linha 17" y1="8.749" x2="8.749" transform="translate(0 0)" fill="none" stroke="#fff" stroke-linecap="round" stroke-linejoin="bevel" stroke-width="2.5"/>
                </g>
              </svg>
              Adicionar Tarefa
            </button>
          </div>
          {loading=== true?
          <ToDoListTrello open={open} inProgress={inProgress} finished={finished} listToDoList={list} refresh={refresh}/>
          :''}
          <AddToDoList refresh={refresh} />
        </div>
    </>  )
}

export default ToDoList
