import {useEffect, useState} from 'react'
import './toDolist.css'
import ToDoListPage from './../../components/toDoList/ToDoListPage'
import Api from '../../axios/Axios'
import AddToDoList from '../../components/toDoList/AddToDoList'
const ToDoList = () => {


  const [list,setList] = useState(undefined);
  const [open,setOpen] = useState([]);
  const [inProgress, setInProgress] = useState([]);
  const [finished, setFinished] = useState([]);
  const [loading, setLoading] = useState(false)



  useEffect(()=>{


    const customConfig = {
      headers: {
      'Content-Type': 'application/json',
      "Authorization" : `Bearer ${sessionStorage.getItem('token')}`
      }
  };


    async function loadToDoList(){
      await Api('/user/todolist' ,customConfig).then((data)=>{
        setList(data.data.data)
      })
    }
    loadToDoList()
  },[])


  useEffect(()=>{
    if(list!== undefined){

      list.map((item)=>{
        if(item.status == 1){
        setOpen(open => [...open, item])
        }else if(item.status == 2){
          setInProgress(inProgress => [...inProgress, item])

        }else if(item.status ==3){
          setFinished(finished => [...finished, item])

        }
      })
      setLoading(true)

    }
  },[list])






  return (



    <>  
        <div className='todolist-page'>
          <button><img src='./../../../img/icons/iconadd.svg' alt="" /></button>
          {loading=== true?
          <ToDoListPage open={open} inProgress={inProgress} finished={finished} listToDoList={list}/>
          :''}
          <AddToDoList/>
        </div>
    </>  )
}

export default ToDoList
