import {useState, useEffect} from 'react'
import Api from '../../axios/Axios';
import './Finance.css'
import Chart from 'chart.js/auto';



const Finance = () => {

  const [loading,setLoading] = useState(false);
  const [finance,setFinance] = useState(undefined)
  const [aCategory,setACategory] =useState(0)
  const [bCategory,setBCategory] =useState(0)
  const [cCategory,setCCategory] =useState(0)
  const [dCategory,setDCategory] =useState(0)
  const [eCategory,setECategory] =useState(0)
  const [total,setTotal]= useState(0)
  const [userGoals, setUserGoals]=useState(0)
  const [showAdd, setAddShow] = useState(false)

  const customConfig = {
    headers: {
    'Content-Type': 'application/json',
    "Authorization" : `Bearer ${sessionStorage.getItem('token')}`
    }
};

useEffect(()=>{
  async function loadToDoList(){
    await Api('/user/finance' ,customConfig).then((data)=>{
      setFinance(data.data)
      setLoading(true)
    })
  }
  loadToDoList()
},[])


let a = []
let b = []
let c = []
let d = []
let e = []


useEffect(()=>{

  if(finance !== undefined){
    let res = 0
    finance.map((item)=>{
      res = res+ item.value
      setTotal(res)
      if(item.category == 1){
        a.push(item.value)
      } else if(item.category==2){
        b.push(item.value)
      } else if(item.category==3){
        c.push(item.value)
      } else if(item.category==4){
        d.push(item.value)
      } else if(item.category==5){
        e.push(item.value)
      }
    })
  }



},[finance])

function calc(category){
  let res  = 0
  if(category.length>0){
    for(let i = 0; i<category.length; i++){
      res = res+ category[i]
    }
  }
  return res

}


useEffect(()=>{
  if(a.length>0){
   setACategory((calc(a)))
  }
},[a])

useEffect(()=>{
  if(b.length>0){
   setBCategory((calc(b)))
  }
},[b])

useEffect(()=>{
  if(c.length>0){
   setCCategory((calc(c)))
  }
},[c])

useEffect(()=>{
  if(d.length>0){
   setDCategory((calc(d)))
  }
},[d])

useEffect(()=>{
  if(e.length>0){
   setECategory((calc(e)))
  }
},[e])


useEffect(()=>{


if( userGoals>0){
const ctx2 = document.getElementById('myChart2');
if(ctx2!== undefined){


  let chartStatus = Chart.getChart("myChart2"); // <canvas> id
  if (chartStatus != undefined) {
    chartStatus.destroy();
  }



var myChart2 = new Chart(ctx2, {
  type: 'doughnut',
  data: {
      labels: ['Prenejados', 'Investido'
  ],
      datasets: [{
          backgroundColor:[
              '#F5F4F4',
              '#9EC36E'],
          data: [(userGoals) , total],
          borderRadius:[0,12],
          borderWidth:6,
          borderColor: ['#34efae00', '#9EC36E'],
          borderAlign:'center',
          spacing:-2,
      }]
  },
  options: {
