import {useState} from 'react'
import './Card.css';

const Card = ({title,id,category,description, status}) => {


  const [show,setShow] = useState(false)
  let color = ''

  if(status == 1){
    status = 'Aberto'
    color = 'var(--1)'
  } else if(status ==2){
    status = 'Em progresso'
    color = 'var(--2)'

  } else if(status ==3){
    status = 'Concluido'
    color = 'var(--3)'

    }

  function todolistMore(){
    if(show === true){
      setShow(false)
    } else{
      setShow(true)
    }
    console.log('klasdflkasdflkalsdkfklsd')
  }

  return (


      <div onClick={()=>{todolistMore()}} className={`todolist-item ${status == true? 'todolist-comply':''}`} key={id}>
            <div className={`todolist-color-${category}`}></div>
            <div className="todolist-text">
              <div className="todolist-status">
                 {status!== undefined? 
                 <>
                  <div style={{backgroundColor:color}} className={`todolist-status`}></div>
                    <p>{status}</p>
                 </>
                 :''} 
              </div>
              <div className="todolist-container">
                <p>{title}</p>
                   {description.split('') !== undefined ? 
                    <p className="todolist-description"> 
                      {description.split('').length < 37 ? description :
                      description.split('').slice(0,40).join('') +'...'
                    }
                    </p>
                    :''}
              </div>
            </div>
  </div>
  )
}

export default Card
