import {useState, useEffect,useRef}from 'react'
import Chart from 'chart.js/auto';
import './Money.css'
import Skeleton, { SkeletonTheme } from 'react-loading-skeleton'



const MoneyFull = () => {
  const [data,setData ]= useState(undefined);
  const [color,setColor] = useState('#9dc3660d')
  const [colorBorder,setColorBorder] = useState('#9DC366')
  const [loading,setLoading] = useState(false)
  const [interval, setInterval] = useState(1)

  useEffect(()=>{
    async function requestApi () {
      let  api = "https://economia.awesomeapi.com.br/json/daily/EUR-BRL/15"
      fetch(api).then(response => response.json()).then((result) => 
      {setData(result) 
            setLoading(true)}
            )
            .catch((error)=>{
              setTimeout(() => {
                setData(undefined)
                  requestApi()
                  console.log('Try Again')
              }, 5000);
          })
    }
    requestApi()
  },[])




  const ctx = document.getElementById('moneyFullChar');

  if(data!== undefined){
    let chartStatus = Chart.getChart("moneyFullChar"); // <canvas> id
    if (chartStatus != undefined) {
      chartStatus.destroy();
    }

    if(interval ==1){
      const today = new Chart(ctx, {
        type: 'line',
        data: {
            labels: ['7 day go', '6 day go', '5 day go', '4 day go', '3 day go', '2 day go', '1 day go', 'Today'],
            datasets: [{
                label: "EUR",
                display: true,
                data: [ data[8].ask, data[7].ask, data[6].ask, data[5].ask, data[4].ask, data[3].ask, data[2].ask, data[1].ask],
                pointRadius: 0,
                backgroundColor: [
                   `${color}`
                  ],
                borderColor: colorBorder,
                borderWidth: 4,
                tension: 0.4, 
            }]
        },
        options: {
            fill: true,
            options:{
              aspectRatio:2/1 //(width/height)
         },
            plugins: {
                labels: {
                    display: false,
                },
                legend: {
                    display: false,
                },
            },
            scales: {
                y: {
                    display: true,                     
                },
                x: {
                    display: true ,
                }

            }
        },
        responsive: true,
        maintainAspectRatio: false,
        scales: {
            yAxes: [{
                ticks: {
                    beginAtZero:true,
                    stepSize: 1
                }
            }]
        }
    });

    }



  }


  useEffect(()=>{

    if(data !==undefined){

        if(data[1].ask<data[2].ask){
          setColor('#9dc3660d')
          setColorBorder('#9DC366')

        } else{
          setColor('#FA385F0a')
          setColorBorder('#FA3850')
        }

  }
  },[data])








  return (
    <div className='moneyFull-component'>

      {loading == true? 
          <>

            <div className='moneyFullChar-Box'>
              <div className="moneyFullChar-interval-box">
                <p>Intervalo de Tempo</p>
                <select onChange={(e)=>{setInterval(e.target.value)}}>
                  <option value="1">Hojé</option>
                  <option value="2">Últimos 7 dias</option>
                  <option value="3">Útimos 30 Dias</option>
                </select>
              </div>

               <canvas id="moneyFullChar"></canvas>

            </div>
            </>
        :
        <SkeletonTheme baseColor="var(--8)" highlightColor="var(--11)">
            <Skeleton style={{width:'110px',height:'20px',margin:'10px'}}/>
            <Skeleton style={{width:'130px',height:'30px',margin:'0px 10px'}}/>
            <Skeleton style={{width:'310px',height:'80px',margin:'10px 0px 0px 0px',opacity:'0.4'}}/>

          </SkeletonTheme>

        }
    </div>
  )
}

export default MoneyFull
