import {useEffect, useState} from 'react'
import './ListArticles.css'
import Api from '../../axios/Axios'
import { useParams } from 'react-router-dom'
import { Link } from 'react-router-dom'
import PrincipalArticles from './PrincipalArticles'


const ListArticles = ({user}) => {

    let params = useParams()
    const [articles, setArticles] = useState([]);
    const category= params.id
    const search= params.search;
    const article= params.article;
    const [page,setPage]= useState(1)
    const [btnNext ,setBtnNext] = useState(false)
    const [btnBack ,setBtnBack] = useState(false)



    const intervals = [
      { label: 'ano', seconds: 31536000 },
      { label: 'mês', seconds: 2592000 },
      { label: 'dia', seconds: 86400 },
      { label: 'hora', seconds: 3600 },
      { label: 'minuto', seconds: 60 },
      { label: 'segundo', seconds: 1 }
    ];

    function timeSince(date) {
      const seconds = Math.floor((Date.now() - date.getTime()) / 1000);
      const interval = intervals.find(i => i.seconds < seconds);
      const count = Math.floor(seconds / interval.seconds);
      return `${count} ${interval.label}${count !== 1 ? 's' : ''} atrás.`;
    }



    useEffect(()=>{
      setPage(1)

      },[params])



      function checKnext(value){
        if(page==1){
          setBtnBack(false)
        } else{
          setBtnBack(true)
        }
        if(value.length >6){

          setBtnNext(true)
        }}


      useEffect(()=>{
        setBtnNext(false)
        if(user!==undefined){


          if(article !== undefined){
            async function getArticle(){
              await Api.get(`/blog/article?article=${article}`)
                  .then((data)=>{
                    setArticles([data.data])
                    checKnext(data.data)
                  })
                }
                getArticle()

          } else{
                        if(search !== undefined){
                          async function getArticles(){
                            await Api.get(`/blog/search?country=${user.countryId}&search=${search}`).then((data)=>{
                              setArticles(data.data)
                              checKnext(data.data)


                            })

                          }
                          getArticles()  
                        } else{

                                      if(category === undefined){
                                        if(user.countryId !== undefined){

                                          async function getArticles(){
                                            await Api.get(`/blog/articles?country=${user.countryId}&page=${page}`).then((data)=>{
                                              setArticles(data.data)
                                              checKnext(data.data)


                                            })


                                          }
                                          getArticles()
                                      }
                                  } else{
                                          if(user.countryId !== undefined){

                                              async function getArticles(){
                                                  await Api.get(`/blog/articles?country=${user.countryId}&category=${category}&page=${page}`).then((data)=>{
                                                    setArticles(data.data)
                                                    checKnext(data.data)


                                                  })
                                              }
                                              getArticles()
                                          }
                                  }

                  }
  }
  }
    },[user,page, params])  


    function loadingMoreArticleBtn(){

      window.scrollTo(0, document.body.scrollHeight || document.documentElement.scrollHeight);

      setPage(page+1)
      topFunction()

    }




function topFunction() {
  document.body.scrollTop = 0; 
  document.documentElement.scrollTop = 0; 
}

return (
      <div >
        {articles.length> 0 ? 
            <div className='blog-articles-div'>
                <PrincipalArticles item={articles[0]}/>         
              <div className='other-articles'>

              {articles.splice(1).map((item)=>(
                <div className='blog-article-box' key={item.id}>
                  <Link to={`/blog/article/${item.id}`}>
                    <div style={{backgroundImage:`url(${item.img})`}} className="article-poster"></div>

                  </Link>
                    <div className="blog-article-container">
                        <Link onClick={()=>{setPage(1)}} to={`/blog/category/${item.categoryId}`}>
                            <h5 className='princial-article-category'>{item.category.title}</h5>
                          </Link> 
                          <Link to={`/blog/article/${item.id}`}>
                        <h5 className="article-title">{item.title}</h5>

                          </Link>
                          <p>{item.description.split('').slice(0,60).join('')}...</p>

                        <div className="article-time">
                                      <img src="./../../../img/icons/icontime.svg" />
                                        <p className="princial-article-time">{timeSince(new Date(item.createdAt))}</p>

                                  </div>

                    </div>

                </div>

            ))}

                            </div>
        </div>
            :''
    }
          <div className="blog-btn-controller">
            {btnBack == true? 
              <button onClick={()=>{setPage(page-1)}} >Voltar</button>:''}
              {btnNext == true? 
              <button onClick={()=>{loadingMoreArticleBtn()}} >Próximo</button>:''}

          </div>
    </div>

  )
}

export default ListArticles
