import {useState} from 'react'
import './Card.css';

const Card = ({title,id,category,description, status}) => {


  const [show,setShow] = useState(false)
  let color = ''

  if(status == 1){
    status = 'Aberto'
    color = 'var(--1)'
  } else if(status ==2){
    status = 'Em progresso'
    color = 'var(--2)'

  } else if(status ==3){
    status = 'Concluido'
    color = 'var(--3)'

    }


    let colorCategory = ''
  if(category == 'shop'){
    colorCategory = 'var(--2)'
  }  else if(category == 'documents'){
    colorCategory = 'var(--3)'
  }  else if(category == 'health'){
    colorCategory = 'var(--1)'
  }  else if (category == 'others'){
    colorCategory = 'var(--4)'
  }

  function todolistMore(){
    if(show === true){
      setShow(false)
    } else{
      setShow(true)
    }
  }

  return (


      <div onClick={()=>{todolistMore()}} className={`todolist-item ${status == true? 'todolist-comply':''}`} key={id}>
            <div style={{backgroundColor:colorCategory}} className={`todolist-color`}></div>
            <div className="todolist-text">
              <div className="todolist-status">
                 {status!== undefined? 
                 <>
                  <div style={{backgroundColor:color}} className={`todolist-status-cicle`}></div>
                    <p>{status}</p>
                 </>
                 :''} 
              </div>
              <div className="todolist-container">
                <h5>{title}</h5>
                {show == false ? 
                   description.split('') !== undefined ? 
                    <p className="todolist-description"> 
                      {description.split('').length < 37 ? description :
                      description.split('').slice(0,40).join('') +'...'
                    }
                    </p>
                    :''
                    :<>
                    <p className="todolist-description">
                      {description}
                      </p>
                      <div className="todolist-btns-controler">
                          <div className="todolist-btn-edit"></div>
                      </div>

                    </>
                      }
              </div>
            </div>
  </div>
  )
}

export default Card
