import {useEffect,useState} from 'react'
import './Study.css'
import Api from './../../axios/Axios'


const Study = () => {

  const [listStudy, setListStudy] = useState(undefined);
  const [itemCount,setItemCount]= useState(0)
  const [page,setPage]= useState(1)
  const [responserStatus, setResponserStatus] = useState(false)




  const synth = window.speechSynthesis;


  function speak(value) {
    synth.lang = "en";
    const voices = synth.getVoices();
    const utterThis = new SpeechSynthesisUtterance(value);
    utterThis.voice = voices[7];
    if (synth.speaking) {
      synth.cancel();
    }
    utterThis.addEventListener('error', () => {
      console.error('SpeechSynthesisUtterance error');
    });
    utterThis.rate = 0.8;
    synth.speak(utterThis);

  }

  const token = sessionStorage.getItem('token');
  useEffect(()=>{


    async function loadList(){

      const customConfig = {
        headers: {
        'Content-Type': 'application/json',
        "authorization" : `Bearer ${token}`
        }
    };
      await Api.get(`/study?page=${page}`,customConfig).then((data)=>{

        setListStudy(data.data)
        console.log(listStudy)

      })
    }
    loadList()


  },[])

  async function editStudy(level){
    console.log(listStudy)

    const body = {level: level, questionId:listStudy[itemCount].id}

    const token = sessionStorage.getItem('token');
    const customConfig = {
      headers: {
        'Content-Type': 'application/json',
        "authorization" : `Bearer ${token}`
      }
    };
    console.log(customConfig)
    await Api.patch(`/study`,body, customConfig).then((data)=>{

      console.log(data)

    })
  }




  return (
    <div className='study-box'>
                 <div className="box-header">
                <h6>Lista de Tarefas</h6>
                <img src="./../../../img/icons/addbtn.svg" alt="Adicionar" />
          </div>
          {listStudy !== undefined? <>

            <div>
              <div>
                  {responserStatus === true? <p>{listStudy[itemCount].portuguese}</p>: <p></p>}
                  <h1>{listStudy[itemCount].english}</h1>
                  <button onClick={()=>{speak(listStudy[itemCount].english)}} className="study-listen">Escultar</button>
              </div>
              <div className="study-btn-levels">
                  <button onClick={()=>{setResponserStatus(true)}}>Ver tradução</button>
                  <button onClick={()=>{editStudy(2)}}>Dificl</button>
                  <button onClick={()=>{editStudy(1)}}>Médio</button>
                  <button onClick={()=>{editStudy(0)}}>Fácil</button>

              </div>
            </div>

          </>




          :<></>}
    </div>
  )
}

export default Study
