        c.push(item.value)
      } else if(item.category==4){
        d.push(item.value)
      } else if(item.category==5){
        e.push(item.value)
      }
    })
  }



},[finance])

function calc(category){
  let res  = 0
  if(category.length>0){
    for(let i = 0; i<category.length; i++){
      res = res+ category[i]
    }
  }
  return res

}


useEffect(()=>{
  if(a.length>0){
   setACategory((calc(a)))
  }
},[a])

