import {useState, useEffect,useRef}from 'react'
import Chart from 'chart.js/auto';
import './Money.css'
import { getRelativePosition } from 'chart.js/helpers';



const Money = () => {

  const ctx = useRef()
  const data = {
    labels: ['Red', 'Blue', 'Yellow', 'Green', 'Purple', 'Orange'],
    datasets: [{
        label: '# of Votes',
        data: [12, 19, 3, 5, 2, 3],
        backgroundColor: [
            'rgba(255, 99, 132, 0.2)',
            'rgba(54, 162, 235, 0.2)',
            'rgba(255, 206, 86, 0.2)',
            'rgba(75, 192, 192, 0.2)',
            'rgba(153, 102, 255, 0.2)',
            'rgba(255, 159, 64, 0.2)'
        ],
        borderColor: [
            'rgba(255, 99, 132, 1)',
            'rgba(54, 162, 235, 1)',
            'rgba(255, 206, 86, 1)',
            'rgba(75, 192, 192, 1)',
            'rgba(153, 102, 255, 1)',
            'rgba(255, 159, 64, 1)'
        ],
        borderWidth: 1
    }]
}



const chart = new Chart(ctx, {
  type: 'line',
  data: data,
  options: {
    onClick: (e) => {
      const canvasPosition = getRelativePosition(e, chart);

      // Substitute the appropriate scale IDs
      const dataX = chart.scales.x.getValueForPixel(canvasPosition.x);
      const dataY = chart.scales.y.getValueForPixel(canvasPosition.y);
    }
  }
});

useEffect(()=>{
  async function requestApi () {
    console.log('kalskdls')
    let  api = "https://economia.awesomeapi.com.br/json/daily/EUR-BRL/15"
    fetch(api).then(response => response.json()).then(result => console.log(result)).catch(()=>{console.log(false)});
    console.log(api)
  }
  requestApi()
},[])

  return (
    <div className='money-div'>
            <div className="box-header">
                <h6>Cotação</h6>
            <img src="./../../../img/icons/btnpurper.svg" alt="Ver mais" />
            </div>
            <div>
                <canvas ref={ctx} id="myChart" width="400" height="400"></canvas>

            </div>
    </div>
  )
}

export default Money
