import {useEffect, useState} from 'react'
import './Perfil.css'
import Skeleton, { SkeletonTheme } from 'react-loading-skeleton'
import Api from '../../axios/Axios'


const Perfil = ({userPhoto}) => {

  const [loading,setLoading] = useState(false)
  const [user,setUser] = useState([])


  const [photo, setPhoto] = useState(undefined)


  useEffect(()=>{

    if(userPhoto!== undefined){
      setLoading(true)
    }

  },[userPhoto])

  useEffect(()=>{
    async function getUser(){

     await Api.get('/user' , { headers: {"Authorization" : `Bearer ${sessionStorage.getItem('token')}`} })
      .then((res) => {
      setUser((res.data))     
    })
  }

  getUser()
},[])


  return (
    <div className='perfil-box'>
      {loading == true? 
        <>
          <h5>{user.name}</h5>
          <div style={{backgroundImage: `url(./../../../img/user/${userPhoto})`}}  className="photo-user">
            <img src="./../../../img/icons/btnpurper.svg" alt="Ver mais" />
          </div>
      </>
      :
      <SkeletonTheme baseColor="var(--8)" highlightColor="var(--11)">
                    <Skeleton style={{width:'50px',height:'50px',margin:'10px -0px 00px 0px', borderRadius:'40px'}}/>

        </SkeletonTheme>
      }
    </div>
  )
}

export default Perfil
