import {useEffect,useState} from 'react'
import './Study.css'
import Api from './../../axios/Axios'

const Study = () => {

  const [listStudy, setListStudy] = useState(undefined);
  const [page,setPage]= useState(0)
  const [responserStatus, setResponserStatus] = useState(false)




  const synth = window.speechSynthesis;


  function speak(value) {
    synth.lang = "en";
    const utterThis = new SpeechSynthesisUtterance(value);
    if (synth.speaking) {
      synth.cancel();
    }
    utterThis.addEventListener('error', () => {
      console.error('SpeechSynthesisUtterance error');
    });
    utterThis.rate = 0.8;
    synth.speak(utterThis);
  }

  const token = sessionStorage.getItem('token');
  useEffect(()=>{


    async function loadList(){

      const customConfig = {
        headers: {
        'Content-Type': 'application/json',
        "Authorization" : `Bearer ${token}`
        }
    };
      await Api.get('/study',customConfig).then((data)=>{

        setListStudy(data.data)
        console.log(listStudy)

      })
    }
    loadList()


  },[])

  async function editStudy(level){

    const customConfig = {
      headers: {
      'Content-Type': 'application/json',
      "Authorization" : `Bearer ${token}`
      }
  };
    await Api.path(`/study?leve=${level}`,customConfig).then((data)=>{

      setListStudy(data.data)
      console.log(listStudy)

    })
  }
  editStudy()




  return (
    <div className='study-box'>
                 <div className="box-header">
                <h6>Lista de Tarefas</h6>
                <img src="./../../../img/icons/addbtn.svg" alt="Adicionar" />
          </div>
          {listStudy !== undefined? <>

            <div>
              <div>
                  {responserStatus === true? <p>{listStudy[page].portuguese}</p>: <p></p>}
                  <h1>{listStudy[page].english}</h1>
                  <button onClick={()=>{speak(listStudy[page].english)}} className="study-listen">Escultar</button>
              </div>
              <div className="study-btn-levels">
                  <button onClick={()=>{setResponserStatus(true)}}>Ver tradução</button>
                  <button>Dificl</button>
                  <button>Médio</button>
                  <button>Fácil</button>

              </div>
            </div>

          </>




          :<></>}
    </div>
  )
}

export default Study
