import {useState, useEffect} from 'react'

const ForgetPassword = ({closeMessage}) => {

    const [animation,setAnimation] = useState('')
    const [bgAnimation,setBgAnimation] = useState('')


    useEffect(()=>{
            setTimeout(() => {
                setAnimation('message-box-animation')
                setBgAnimation('message-bg-animation')

            }, 10);
    },[])
  return (
    <div className='message-page'>
    <div className={`message-page-bg ${bgAnimation}`} onClick={()=>{closeMessage()}}></div>
    <div className={`message-box ${animation}`}  >
        <h3>Esqueci a senha.</h3>
        <p>Enviaremos um email com um link para reformular a sua senha. Por favor check o seu email.</p>
        <button
        className={`btn-success space-button`}
         onClick={()=>{closeMessage()}}>Enviar</button>
    </div>
</div>
  )
}

export default ForgetPassword
