import {useEffect, useState} from 'react'
import './FInance.css'
import Api from './../../axios/Axios'
import Chart from 'chart.js/auto';



const Finance = ({goal}) => {

  const token = sessionStorage.getItem('token');

  const [finance,setFinance] = useState(undefined)
  const [total,setTotal] = useState(0)
  const [lessToGoal ,setLessToGoal] = useState(0)

  useEffect(()=>{


    const customConfig = {
      headers: {
      'Content-Type': 'application/json',
      "Authorization" : `Bearer ${token}`
      }
  };


    async function loadToDoList(){
      await Api('/user/finance' ,customConfig).then((data)=>{
        setFinance(data.data.data)
      })
    }
    loadToDoList()
  },[])

  console.log(finance)


  useEffect(()=>{

    if(finance !== undefined){

        setTotal(calc())


    }

  },[finance])

  function calc(){
    let total = 0
    finance.map((item)=>{
        total = total + item.value
    })
    return total
  }


  useEffect(()=>{
    setLessToGoal((total-goal)*(-1) || 0)
  },[total])






  const ctx = document.getElementById('myChart2');
  if(finance!== undefined){
    let chartStatus = Chart.getChart("myChart2"); // <canvas> id
    if (chartStatus != undefined) {
      chartStatus.destroy();
    }}






  var myChart2 = new Chart(ctx, {
    type: 'doughnut',
    data: {
        labels: ['Falt', 'Total'
    ],
        datasets: [{
            backgroundColor:[
                '#F5F4F4',
                '#34EFAE'],
            data: [(lessToGoal) , total],
            borderRadius:[0,12],
            borderWidth:10,
            borderColor: ['#34efae00', '#34EFAE'],
            borderAlign:'center',
            spacing:-2,
        }]
    },
    options: {
        responsive: true,
        plugins: {
            labels: {
                display: false,
            },
            legend: {
                display: false,
            },
        },
        scales: {

            y: {
                display: false,
            },
            x: {
                display: false ,
            }           
        }
    },
});





  console.log(lessToGoal)
  return (
    <div className='finance-box'>
      <div className="box-header">
                <h6>Lista de Tarefas</h6>
                <img src="./../../../img/icons/addbtn.svg" alt="Adicionar" />
          </div>
          <div >
            {finance !== undefined ? 
            <>

                <div className="finance-left">
                  <div className="finance-left-top">
                    <p>Investido</p>
                    <h2>{total}</h2>
                  </div>
                  <div className="finance-left-bottom">
                    <div>
                      <p>Faltam:</p>
                      <h4>{lessToGoal}</h4>
                    </div>
                    <div>
                      <p>Planejamento:</p>
                      <h4>{goal}</h4>
                    </div>
                  </div>
                </div>
                <div className="finance-right">
                <canvas id="myChart2" width="100px" height="50px"></canvas>

                </div>
            </>


            :''}

            </div>
    </div>
  )
}

export default Finance
