import React from 'react'
import './loginComponent.css'
import {useState, useEffect} from 'react'
import Api from '../../axios/Axios'
import Messages from '../messages/Messages'


const LoginComponent = () => {
    const [message, setMessage] = useState([]) 
    const [loginHeader, setLoginHeader ] = useState('login')
    const [loginPassword, setLoginPassword ] = useState('password')
    const [loginPassword2, setLoginPassword2 ] = useState('password')
    const [loginPassword3, setLoginPassword3 ] = useState('password')
    const [passwordCreateValue, setPasswordCreateValue] = useState('')
    const [require, setRequire]=useState(false)
    const [require2, setRequire2]=useState(false)
    const [remember, setRemember] = useState(true)

    const [loginEmailValue, setLoginEmailValue] = useState('')
    const [loginPasswordlValue, setLoginPasswordlValue] = useState('')

    // useStates Requirements 
    const [min, setMin] = useState(false)
    const [upper, setUpper] = useState(false)
    const [lower, setLower] = useState(false)
    const [number, setNumber] = useState(false)
    const [passwordOk, setPasswordOk] = useState(false)
    const [samePassword, setSamePassword] = useState(false)




    // password requires

    function passwordCreateChange(value){
        setPasswordCreateValue(value)

        if (value.search(/[a-z]/) >= 0) {setLower(true)} else{setLower(false)}
        if(value.search(/[A-Z]/) >= 0) {setUpper(true)} else{setUpper(false)}
        if (value.search(/[0-9]/) >= 0) {setNumber(true)} else{setNumber(false)}
        if (value.length >= 8) {setMin(true)} else{setMin(false)}
        if(lower === true && upper ===true && number === true && min === true){setPasswordOk(true)}else{setPasswordOk(false)}    

    }

    function passwordSame(value){
        if(passwordCreateValue == value){
            setSamePassword(true)
        } else{
            setSamePassword(false)        
        }
    }


    function headerChange(value){
        setLoginHeader(value)

    }

    function rememberChange(){
        if(remember === false){
            setRemember(true)
        }else{ setRemember(false)}
    }


    //login Axios


    async function userLogin(){
        const body = {email: loginEmailValue, password:loginPasswordlValue}
        const customConfig = {
            headers: {
            'Content-Type': 'application/json'
            }
        };
        const res = Api.post('user/login',{email:loginEmailValue,password:loginPasswordlValue},customConfig).then((data)=>{console.log(data)})
    }

  return (
    <>
    {message !== null ? <Messages title={'teste'}/> : ''}


    <div className='login-right'>
        <div className="login-header">
            <button onClick={()=>{headerChange('login')}} className={loginHeader === 'login' ? 'login-header-active':''}>Login</button>
            <button onClick={()=>{headerChange('singup')}} className={loginHeader === 'singup' ? 'login-header-active':''}>Cadastrar</button>
        </div>
        {loginHeader === 'login' ? 
            <div className="login-form">
                <p className="login-form-text">Entre com seus dados de acesso.</p>
                <label>
                    <h5>Email:</h5>
                    <input onChange={(e)=>{setLoginEmailValue(e.target.value)}} type="text" id="login-email-input" placeholder='exemplo@google.com' />
                </label>
                <label>
                    <h5>Senha::</h5>
                    <input onChange={(e)=>{setLoginPasswordlValue(e.target.value)}} type={loginPassword} id="login-email-input"  placeholder='*******'/>
                    <img
                    className='password-eyes'
                    onMouseOver={()=>{setLoginPassword('text')}} 
                    onMouseLeave={()=>{setLoginPassword('password')}}
                    src="../../../img/icons/eyes.svg" 
                    alt="Ver Password" />
                </label>
                <div className="remember" onClick={()=>{rememberChange()}}>
                    <div className={`remember-btn ${remember === true? 'remember-btn-box-selected':''}`}>
                        <div className={`remember-btn-cicle ${remember === true? 'remember-btn-selected':''}`}></div>
                    </div>
                    <p>Lembrar senha.</p>
                </div>
                <button onClick={()=>{userLogin()}} className='btn-success'>Entrar</button>
                <button className='forgetPassword'>Esqueceu a senha?</button>
            </div>
                :
            <div className="singup-form">
                    <p className="login-form-text">Preencha com seus dados.</p>
                <label>
                    <h5>Email:</h5>
                    <input type="text" id="login-email-input" placeholder='exemplo@google.com' />
                </label>
                <label>
                    <h5>Senha:</h5>
                    <input 
                    type={loginPassword2} 
                    id="login-email-input" 
                    placeholder='*******'
                    onFocus={()=>{setRequire(true)}} 
                    onBlur={()=>{setRequire(false)}}
                    onChange={(e)=>{passwordCreateChange(e.target.value)}}
                    />
                    <img
                    className='password-eyes'
                    onMouseOver={()=>{setLoginPassword2('text')}} 
                    onMouseLeave={()=>{setLoginPassword2('password')}}
                    src="../../../img/icons/eyes.svg" 
                    alt="Ver Password" />
                </label>
                <div className={`require-password ${require === false ? 'opacityNone':''}`}>
                    <p className={min == true? 'require-password-sucess':''}>Minimo 8 caracteres.</p>
                    <p className={upper == true? 'require-password-sucess':''}>Letra maiúscula</p>
                    <p className={lower == true? 'require-password-sucess':''}>Letra minúscula</p>
                    <p className={number == true? 'require-password-sucess':''}>Número</p>
                </div>
                <label>
                    <h5>Confirme a senha:</h5>
                    <input 
                    type={loginPassword3} 
                    id="login-email-input"  
                    placeholder='*******'
                    onFocus={()=>{setRequire2(true)}} 
                    onBlur={()=>{setRequire2(false)}}
                    onChange={(e)=>{passwordSame(e.target.value)}}
                    />

                    <img
                    className='password-eyes'
                    onMouseOver={()=>{setLoginPassword3('text')}} 
                    onMouseLeave={()=>{setLoginPassword3('password')}}
                    src="../../../img/icons/eyes.svg" 
                    alt="Ver Password" />
                </label>
                <div className={`require-password confirm-password ${require2 === false ? 'opacityNone':''}`}>
                    <p className={samePassword === true? 'require-password-sucess':''}>A senhas devem ser iguais.</p>

                </div>

                <button className='btn-success'>Cadatrar</button>
            </div>

                }

    </div>
    </>
  )
}

export default LoginComponent
