import {useEffect, useState} from 'react'
import './ListArticles.css'
import Api from '../../axios/Axios'
import { useParams } from 'react-router-dom'
import { Link } from 'react-router-dom'


const ListArticles = ({user}) => {

    let params = useParams()
    const [articles, setArticles] = useState([]);
    const category= params.id
    const search= params.search;
    const article= params.article;
    const [page,setPage]= useState(1)

console.log(article)



    useEffect(()=>{
      setPage(1)

      },[params])

      useEffect(()=>{

        if(user!==undefined){

          if(article !== undefined){
            async function getArticle(){
              await Api.get(`/blog/article?article=${article}`)
                  .then((data)=>{
                    setArticles(data.data)
                  })
            }

          }

        if(search !== undefined){
          async function getArticles(){
            await Api.get(`/blog/search?country=${user.countryId}&search=${search}`).then((data)=>{
              setArticles(data.data)
            })

          }
          getArticles()  
        } else{

        if(category === undefined){
          if(user.countryId !== undefined){

            async function getArticles(){
              await Api.get(`/blog/articles?country=${user.countryId}&page=${page}`).then((data)=>{
                setArticles(data.data)
              })


            }
            getArticles()
        }
    } else{
        if(user.countryId !== undefined){

            async function getArticles(){
                await Api.get(`/blog/articles?country=${user.countryId}&category=${category}&page=${page}`).then((data)=>{
                  setArticles(data.data)
                })
            }
            getArticles()
        }
    }}
  }
    },[user,page])   
    function loadingMoreArticleBtn(){

      window.scrollTo(0, document.body.scrollHeight || document.documentElement.scrollHeight);

      setPage(page+1)
      topFunction()

    }


function topFunction() {
  document.body.scrollTop = 0; 
  document.documentElement.scrollTop = 0; 
}
console.log(articles)

  return (
    <div >
                {articles.length> 0 ? 

              articles.map((item)=>(
                <article className='blog-article-box' key={item.id}>
                    <div style={{backgroundImage:`url(${item.img})`}} className="article-poster"></div>
                    <h2 className="article-title">{item.title}</h2>
                    <Link onClick={()=>{setPage(1)}} to={`/blog/category/${item.categoryId}`}>
                        <h5>{item.category.title}</h5>
                      </Link> 
                    <p className="article-description">{item.description}</p>
                </article>

            ))
            :''}
            <button onClick={()=>{loadingMoreArticleBtn()}} className='btn-loading-more-articles'>Carregar mais</button>
    </div>
  )
}

export default ListArticles
