import {useEffect, useState} from 'react'
import './ListArticles.css'
import Api from '../../axios/Axios'
import { useParams } from 'react-router-dom'


const ListArticles = () => {

    let params = useParams()
    const [articles, setArticles] = useState(undefined);
    const [category, setCategory] = useState(params.id)



    const [user,setUser] = useState([])
    let token = ''

    const local = localStorage.getItem('token');
    const session = sessionStorage.getItem('token');
    if(!session){
        if(!local){
            window.location.href = '/login'
        } else{
          token = local
            sessionStorage.setItem('token',local)
        }
    } else{
      token = session




    }




    useEffect(()=>{

        getOptions()
        async function getOptions(){
          const config = {
            headers: { Authorization: `Bearer ${token}` }
        };
          await Api.get('/user/useroptions', config).then((data)=>{
            if(!data.data[0]){
              window.location.href = '/createuseroptions'
            } else{
              setUser(data.data[0])

            }
          })
        }
      },[])

      useEffect(()=>{
        console.log(category)
        if(category === undefined){
        if(user.country !== undefined){

            async function getArticles(){
                await Api.get(`/blog/articles?country=${user.country}`).then((data)=>{
                    setArticles(data.data)
                    console.log(data)
                })
            }
            getArticles()
        }
    } else{
        if(user.country !== undefined){

            async function getArticles(){
                await Api.get(`/blog/articles?country=${user.country}&category=${category}`).then((data)=>{
                    setArticles(data.data)
                    console.log(data)
                })
            }
            getArticles()
        }
    }
    },[user])   

  return (
    <div >
                {articles!== undefined ? 

            articles.map((item)=>(
                <article className='blog-article-box' key={item.id}>
                    <div style={{backgroundImage:`url(${item.img})`}} className="article-poster"></div>
                    <h2 className="article-title">{item.title}</h2>
                    <a href={`/blog/category/${item.categoryId}`}>
                          <h5 className="arcitle-category">{item.category.title}</h5>
                      </a> 
                    <p className="article-description">{item.description}</p>
                </article>

            ))
            :''}
    </div>
  )
}

export default ListArticles
