import {useState, useEffect , memo} from "react";
import * as ReactDOM from "react-dom";
import styled from "styled-components";
import { DragDropContext, Droppable, Draggable } from "react-beautiful-dnd";
import Api from "../../axios/Axios";
import Card from "./Card";




function ToDoListPage() {
    const [list,setList] = useState([])
    let token = undefined

    const local = localStorage.getItem('token');
    const session = sessionStorage.getItem('token');


    if(!session){
        if(!local){
            window.location.href = '/login'
        } else{
            token =local
            sessionStorage.setItem('token',local)
        }
    } else{
      token = session




    }




    useEffect(()=>{

        if(token !== undefined){


            const config = {
                headers: { Authorization: `Bearer ${sessionStorage.getItem('token')}` }
            };
            async function getArticle(){
              await Api.get(`/user/todolist`, config)
                  .then((data)=>{
                   setList(data.data.data)
                  })
                }
                getArticle()
            }

        },[token])  





useEffect(()=>{
    function vefiricStatus(){
        list.map((item)=>{
            if(item.status == 'open'){
                open.push(item.id)
            }
        })
    }
    vefiricStatus()
},[list])

let open = []

useEffect(()=>{console.log(open)},[open])


useEffect(()=>{
    if(list !== undefined)
    {

    }


},[list])

let data = {
    tasks: list,
    columns: {
      col1: {
        id: "col1",
        title: "Aberto",
        //taskIds: ["task1", "task2", "task3", "task4"]
        taskIds: open
      },
      col2: {
        id: "col2",
        title: "Em Progresso",
        taskIds: []
      },
      col3: {
        id: "col3",
        title: "Finalizado",
        taskIds: []
      }
    },
    columnOrder: ["col1", "col2", "col3"]
  };
  console.log(data)
  const TaskContainer = styled.div`
    border: 1px solid lightgrey;
    padding: 8px;
    border-radius: 2px;
    margin-bottom: 8px;
    background-color: ${props => (props.isDragging ? "red" : "white")};
    transition: background 0.1s;
  `;




  const Task = memo(({ task, index }) => {
    return (
      <Draggable draggableId={task.id} index={index}>
        {(provided, snapshot) => (


            <TaskContainer
            {...provided.draggableProps}
            {...provided.dragHandleProps}
            ref={provided.innerRef}
            isDragging={snapshot.isDragging}
            >
              <Card title={task.title} description={task.description} category={task.category}>


          </Card>
          </TaskContainer>
        )}
      </Draggable>
    );
  });

  const Container = styled.div`
    margin: 8px;
    border: 1px solid lightgrey;
    border-radius: 2px;
    width: 200px;
    display: flex;
    flex-direction: column;
    background-color: ${props => (props.isDragging ? "lightgreen" : "white")};
  `;
  const Title = styled.h3`
    padding: 8px;
  `;
  const List = styled.div`
    padding: 8px;
    transition: background 0.1s;
    background-color: ${props =>
      props.isDraggingOver ? "lightgrey" : "inherit "};
    flex-grow: 1;
  `;

  const Columns = styled.div`
    display: flex;
  `;



  const Column = memo(({ column, tasks, index }) => (
    <Draggable draggableId={column.id} index={index}>
      {(provided, snapshot) => (
        <Container
          {...provided.draggableProps}
          {...provided.dragHandleProps}
          isDragging={snapshot.isDragging}
          ref={provided.innerRef}
        >
          <Title {...provided.dragHandleProps}>{column.title}</Title>
          <Droppable droppableId={column.id} type="task">
            {(provided, snapshot) => (
              <List
                ref={provided.innerRef}
                {...provided.droppableProps}
                isDraggingOver={snapshot.isDraggingOver}
              >
                {tasks.map((t, i) => (
                  <Task key={t.id} task={t} index={i} />
                ))}
                {provided.placeholder}
              </List>
            )}
          </Droppable>
        </Container>
      )}
    </Draggable>
  ));

  const [state, setState] = useState(data);
  return (
    <DragDropContext
      onDragEnd={({ destination, source, draggableId, type }) => {
        if (!destination) {
          return;
        }
        if (
          destination.droppableId === source.droppableId &&
          destination.index === source.index
        ) {
          return;
        }

        if (type === "column") {
          const newColOrd = Array.from(state.columnOrder);
          newColOrd.splice(source.index, 1);
          newColOrd.splice(destination.index, 0, draggableId);

          const newState = {
            ...state,
            columnOrder: newColOrd
          };
          setState(newState);
        }

        const startcol = state.columns[source.droppableId];
        const endcol = state.columns[destination.droppableId];

        if (startcol === endcol) {
          const tasks = Array.from(startcol.taskIds);
          tasks.splice(source.index, 1);
          tasks.splice(destination.index, 0, draggableId);

          const newCol = {
            ...startcol,
            taskIds: tasks
          };

          const newState = {
            ...state,
            columns: {
              ...state.columns,
              [newCol.id]: newCol
            }
          };

          setState(newState);
          return;
        }
        const startTaskIds = Array.from(startcol.taskIds);
        startTaskIds.splice(source.index, 1);
        const newStart = {
          ...startcol,
          taskIds: startTaskIds
        };
        const endTaskIds = Array.from(endcol.taskIds);
        endTaskIds.splice(destination.index, 0, draggableId);
        const newEnd = {
          ...endcol,
          taskIds: endTaskIds
        };
        const newState = {
          ...state,
          columns: {
            ...state.columns,
            [newStart.id]: newStart,
            [newEnd.id]: newEnd
          }
        };
        setState(newState);
      }}
    >
      <Droppable droppableId="columns" direction="horizontal" type="column">
        {provided => (
          <Columns {...provided.droppableProps} ref={provided.innerRef}>
            {state.columnOrder.map((id, i) => {
              const col = state.columns[id];
              const tasks = col.taskIds.map(taskid => state.tasks[taskid]);
              return <Column key={id} column={col} tasks={tasks} index={i} />;
            })}
            {provided.placeholder}
          </Columns>
        )}
      </Droppable>
    </DragDropContext>
  );
}

export default ToDoListPage
