import {useState, useEffect,useRef}from 'react'
import Chart from 'chart.js/auto';
import './Money.css'
import { getRelativePosition } from 'chart.js/helpers';



const Money = () => {
  const [data,setData ]= useState(undefined);
  const [color,setColor] = useState('#9dc3660f')
  const [colorBorder,setColorBorder] = useState('#9DC366')

  useEffect(()=>{
    async function requestApi () {
      console.log('kalskdls')
      let  api = "https://economia.awesomeapi.com.br/json/daily/EUR-BRL/15"
      fetch(api).then(response => response.json()).then(result => setData(result)).catch(()=>{setData(undefined)});
      console.log(api)
    }
    requestApi()
  },[])




  const ctx = document.getElementById('myChart');
  if(data!== undefined){
    let chartStatus = Chart.getChart("myChart"); // <canvas> id
    if (chartStatus != undefined) {
      chartStatus.destroy();
    }


    const myChart = new Chart(ctx, {
      type: 'line',
      data: {
          labels: [, '3 day go', '2 day go', '1 day go', 'Today'],
          datasets: [{
              label: "EUR",
              display: true,
              data: [data[4].ask, data[3].ask, data[2].ask, data[1].ask, data[0].ask],
              pointRadius: 0,
              backgroundColor: [
                 `${color}`
                ],
              borderColor: colorBorder,
              borderWidth: 2,
              tension: 0.4, 
          }]
      },
      options: {
          fill: true,
          plugins: {
              labels: {
                  display: false,
              },
              legend: {
                  display: false,
              },
          },
          scales: {
              y: {
                  display: false,                     
              },
              x: {
                  display: false ,
              }

          }
      },
      responsive: true,
      maintainAspectRatio: false,
      scales: {
          yAxes: [{
              ticks: {
                  beginAtZero:true,
                  stepSize: 1
              }
          }]
      }
  });

  }









  return (
    <div className='money-div'>
            <div className="box-header">
                <h6>Cotação</h6>
            <img src="./../../../img/icons/btnpurper.svg" alt="Ver mais" />
            </div>
            <div>
            <canvas id="myChart"  width='400px' height="80px"></canvas>

            </div>
    </div>
  )
}

export default Money
