import {useEffect,useState} from 'react'
import './TodoList.css'
import Api from './../../axios/Axios'
const ToDoList = () => {

  const token = sessionStorage.getItem('token');

  const [toDoList,setToDoList] = useState(undefined)

  useEffect(()=>{


    const customConfig = {
      headers: {
      'Content-Type': 'application/json',
      "Authorization" : `Bearer ${token}`
      }
  };


    async function loadToDoList(){
      await Api('/user/todolist' ,customConfig).then((data)=>{
        setToDoList(data.data.data)
      })
    }
    loadToDoList()
  },[])




  return (
    <div className='todolist-box'>
           <div className="box-header">
                <h6>Lista de Tarefas</h6>
                <img src="./../../../img/icons/btnpurper.svg"  />
          </div>
          <div className='todolist-scrol'>
            {toDoList !== undefined ? 
              toDoList.map((item)=>(
                <div className={`todolist-item ${item.status == true? 'todolist-comply':''}`} key={item.id}>
                  <div className={`todolist-category-${item.category}`}></div>
                  <div className="todolist-text">
                    <div className="todolist-status">
                      <div className={`todolist-status-${item.status}`}></div>
                      <p>{item.status}</p>
                    </div>
                    <div className="todolist-container">
                      <p>{item.title}</p>

                      {item.description.split('') !== undefined ? 
                          <p className="todolist-description">

                            {item.description.split('').length < 37 ? item.description :
                            item.description.split('').slice(0,40).join('') +'...'
                          }
                          </p>
                          :''}
                    </div>
                  </div>
                </div>
              ))


            :''}

            </div>
    </div>
  )
}

export default ToDoList
