import React, { useEffect ,useState } from 'react'
import Api from './../../axios/Axios'
import './BlogDashboard.css'

const BlogDashboard = ({countryId}) => {

    const [article, setArticle] = useState()
    const [data,setData] = useState([])
    const [page,setPage] = useState(0)
    const [nextArticleStatus, setNextArticleStatus] = useState(true)
    const [backArticleStatus, setBackArticleStatus] = useState(false)

    useEffect(()=>{
        if(countryId !== undefined){


        console.log(`O pais e ${countryId}`)
        async function loadBlog(){
          const res =   await Api.get(`/blog/articles?country=${countryId}`)

          setArticle(res.data)
        }
        loadBlog()
    }
    },[countryId])





    useEffect(()=>{

        console.log(article)
    },[article])



    function nextBlog(){
        if(page+2 <= article.length){
            setPage(page+1)

        } else{
            console.log('Mais')
            setNextArticleStatus(false)
        }
    }


    function nextBlog(){
        if(page-1 == -1){
            setBackArticleStatus(false)            
        } else{
            console.log('Mais')
            setNextArticleStatus(false)
        }
    }





  return (
    <div>


        <div className='blog-box'>
            <div className="box-header">
                <h6>Blog</h6>
                <img src="./../../../img/icons/btnpurper.svg" alt="Ver mais" />
            </div>
            {article !== undefined ? 
            <div>
                <div className="blog-container">
                    <div style={{backgroundImage: `url("${article[page].img}")`}} className="blog-img"></div>
                    <h3>{article[page].title}</h3>
                    <p>{article[page].description}</p>
                    <button>Continuar lendo</button>
                </div>
            </div>

            : <h1>Não tem</h1>}
        </div>
        {backArticleStatus === true ? <button onClick={()=>{nextBlog()}}>Anterior</button>: ''}
        {nextArticleStatus === true ? <button onClick={()=>{backBlog()}}>Prôximo</button>: ''}
    </div>
  )
}

export default BlogDashboard
