import {useEffect,useState} from 'react'
import './Study.css'
import Api from './../../axios/Axios'

const Study = () => {

  const [listStudy, setListStudy] = useState(undefined);
  const [page,setPage]= useState(0)


  let speech = new SpeechSynthesisUtterance();
  speech.lang = "en";

  let voices = []; // global array

  function speak(value){
    speech.text = value
    voices = window.speechSynthesis.getVoices();

    speech.voice = voices[1];

    window.speechSynthesis.speak(speech);

  }

  window.speechSynthesis.onvoiceschanged = () => {
    // On Voices Loaded
  };

  useEffect(()=>{

    const token = sessionStorage.getItem('token');

    async function loadList(){

      const customConfig = {
        headers: {
        'Content-Type': 'application/json',
        "Authorization" : `Bearer ${token}`
        }
    };
      await Api.get('/study',customConfig).then((data)=>{

        setListStudy(data.data)
        console.log(listStudy)

      })
    }
    loadList()


  },[])




  return (
    <div className='study-box'>
                 <div className="box-header">
                <h6>Lista de Tarefas</h6>
                <img src="./../../../img/icons/addbtn.svg" alt="Adicionar" />
          </div>
          {listStudy !== undefined? <>

            <div>
              <div>
                  <h1>{listStudy[page].english}</h1>
                  <button onClick={()=>{speak(listStudy[page].english)}} className="study-listen">Escultar</button>
              </div>
              <div className="study-btn-levels">
                  <button>Ver tradução</button>
                  <button>Dificl</button>
                  <button>Médio</button>
                  <button>Fácil</button>

              </div>
            </div>

          </>




          :<></>}
    </div>
  )
}

export default Study
