import {useEffect, useState} from 'react'
import './ListArticles.css'
import Api from '../../axios/Axios'
import { useParams } from 'react-router-dom'
import { Link } from 'react-router-dom'


const ListArticles = () => {

    let params = useParams()
    const [articles, setArticles] = useState([]);
    const category= params.id
    const search= params.search;
    const [page,setPage]= useState(1)


    useEffect(()=>{

    },[])



    const [user,setUser] = useState([])
    let token = ''

    const local = localStorage.getItem('token');
    const session = sessionStorage.getItem('token');
    if(!session){
        if(!local){
            window.location.href = '/login'
        } else{
          token = local
            sessionStorage.setItem('token',local)
        }
    } else{
      token = session




    }

useEffect(()=>{
  setPage(1)

},[params])


    useEffect(()=>{

        getOptions()
        async function getOptions(){
          const config = {
            headers: { Authorization: `Bearer ${token}` }
        };
          await Api.get('/user/useroptions', config).then((data)=>{
            if(!data.data[0]){
              window.location.href = '/createuseroptions'
            } else{
              setUser(data.data[0])

            }
          })
        }
      },[params,page])

      useEffect(()=>{

        if(search !== undefined){
          async function getArticles(){
            await Api.get(`/blog/search?country=${user.countryId}&search=${search}`).then((data)=>{
              setArticles(data.data)
            })

          }
          getArticles()  
        } else{

        if(category === undefined){
          if(user.countryId !== undefined){

            async function getArticles(){
              await Api.get(`/blog/articles?country=${user.countryId}&page=${page}`).then((data)=>{
                setArticles(data.data)
              })


            }
            getArticles()
        }
    } else{
        if(user.countryId !== undefined){

            async function getArticles(){
                await Api.get(`/blog/articles?country=${user.countryId}&category=${category}&page=${page}`).then((data)=>{
                  setArticles(data.data)
                })
            }
            getArticles()
        }
    }}
    },[user,page])   
    console.log(articles)

    function loadingMoreArticleBtn(){

       const loadingMoreArticleBtn = document.querySelector(".blog-page-left")
      window.scrollTo(0, document.body.scrollHeight || document.documentElement.scrollHeight);

      setPage(page+1)
      topFunction()

    }

    // Get the button:
let mybutton = document.getElementById("myBtn");

// When the user scrolls down 20px from the top of the document, show the button
window.onscroll = function() {scrollFunction()};

function scrollFunction() {
  if (document.body.scrollTop > 20 || document.documentElement.scrollTop > 20) {
    mybutton.style.display = "block";
  } else {
    mybutton.style.display = "none";
  }
}

// When the user clicks on the button, scroll to the top of the document
function topFunction() {
  document.body.scrollTop = 0; // For Safari
  document.documentElement.scrollTop = 0; // For Chrome, Firefox, IE and Opera
}


  return (
    <div >
                {articles.length> 0 ? 

              articles.map((item)=>(
                <article className='blog-article-box' key={item.id}>
                    <div style={{backgroundImage:`url(${item.img})`}} className="article-poster"></div>
                    <h2 className="article-title">{item.title}</h2>
                    <Link onClick={()=>{setPage(1)}} to={`/blog/category/${item.categoryId}`}>
                      </Link> 
                    <p className="article-description">{item.description}</p>
                </article>

            ))
            :''}
            <button onClick={()=>{loadingMoreArticleBtn()}} className='btn-loading-more-articles'>Carregar mais</button>
    </div>
  )
}

export default ListArticles
