import React, { useState, useEffect } from "react";
import { DragDropContext, Droppable, Draggable } from "react-beautiful-dnd";
import Api from "../../axios/Axios";
import './ToDoListPage.css'


export default function ToDoListPage({open, inProgress, finished}) {


      const [obj, setObj] = useState(
        {

            Todo: {
              name: "Aberto",
              color: "#f29c9690",
              items: open

            },
            In_Progress: {
              name: "Em Progresso",
              color: "#ede89f90",
              items: inProgress
            },
            Done: {
              name: "Finalizado",
              color: "#96d9ad90",
              items: finished
            }

    }
      );







  function event(result) {
    const { destination, source } = result;
    if (!destination) return;
    if (source.droppableId !== destination.droppableId) {
      const newSourceObj = obj[source.droppableId];
      const copiedSObj = [...newSourceObj.items];
      const [reorderObj] = copiedSObj.splice(source.index, 1);

      const newDestinationObj = obj[destination.droppableId];
      const copiedDObj = [...newDestinationObj.items];
      copiedDObj.splice(destination.index, 0, reorderObj);

      setObj({
        ...obj,
        [source.droppableId]: {
          ...newSourceObj,
          items: copiedSObj
        },
        [destination.droppableId]: {
          ...newDestinationObj,
          items: copiedDObj
        }
      });
    } else {
      const newSourceObj = obj[source.droppableId];
      const copiedSObj = [...newSourceObj.items];
      const [reorderObj] = copiedSObj.splice(source.index, 1);
      copiedSObj.splice(destination.index, 0, reorderObj);
      setObj({
        ...obj,
        [source.droppableId]: {
          ...newSourceObj,
          items: copiedSObj
        }
      });
    }
  }

  console.log('lkasdflksdlk')
  useEffect(()=>{
    const config = {
        headers: { Authorization: `Bearer ${sessionStorage.getItem('token')}` }
    };

    async function updateToDoList(id, status, position){
        const body = {id:id, status:status, position:position}
        await Api.patch('/user/todolist/', body, config)
    }


  },[obj.Done])
  return (
      <>
      <div className="toDoList-page-tables">
        <DragDropContext onDragEnd={(result) => event(result)}>
          {Object.keys(obj).map((res, index) => {
            return (
              <Droppable droppableId={`${res}`} key={res}>
                {(provided, snapshot) => (
                  <div className="toDoList-page-simple-table" >
                    <span className="toDoList-page-table-title">
                      {obj[res].name}
                    </span>
                    <span className="toDoList-page-counter">
                      {obj[res].items.length}
                    </span>
                    <div {...provided.droppableProps} ref={provided.innerRef} >
                      {obj[res].items.map((res, index) => {
                        return (
                          <Draggable draggableId={(res.id).toString()} key={res.id} index={index} >
                            {(provided, snapshot) => (
                              <div
                                {...provided.draggableProps}
                                {...provided.dragHandleProps}
                                ref={provided.innerRef}
                                style={{
                                  ...provided.draggableProps.style
                                }}
                              >


                                <div className={`todolist-item ${res.status == true? 'todolist-comply':''}`} key={res.id}>
                  <div className={`todolist-color-${res.category}`}></div>
                  <div className="todolist-text">
                    <div className="todolist-status">
                      <div className={`todolist-status-${res.status}`}></div>
                      <p>{res.status}</p>
                    </div>
                    <div className="todolist-container">
                      <p>{res.title}</p>

                      {res.description.split('') !== undefined ? 
                          <p className="todolist-description">

                            {res.description.split('').length < 37 ? res.description :
                            res.description.split('').slice(0,40).join('') +'...'
                          }
                          </p>
                          :''}
                    </div>
                  </div>
                </div>
                              </div>
                            )}
                          </Draggable>
                        );
                      })}
                      {provided.placeholder}
                    </div>
                  </div>
                )}
              </Droppable>
            );
          })}
        </DragDropContext>
      </div>
    </>
  );
}
