import {useEffect, useState} from 'react'
import './ListCategory.css'
import Api from '../../axios/Axios'
import { Link } from 'react-router-dom'

const ListCategory = () => {


    const [categories, setCategories] = useState(undefined);




      useEffect(()=>{

            async function getCategories(){

                await Api.get('/blog/categories' ).then((data)=>{
                    setCategories(data.data)
                })
            }
            getCategories()

    },[])






  return (
    <div className='blog-list-div'>

        {categories !== undefined? 
        <>

            <h3>Categorias</h3>
            <div className='blog-list-category'>

            <ul>  
                <Link to="/blog"><li>Todas as categorias</li></Link>
                {categories.map((item)=>(
                  <Link key={item.id} to={`/blog/category/${item.id}`}>  <li >{item.title}</li></Link>
                ))}

            </ul>
            </div>
            </>

        :''}

    </div>
  )
}

export default ListCategory
