import {useState,useEffect} from 'react'
import Api from './../../axios/Axios'

const CreateUserOptions = () => {

  const [user,setUser] = useState(null)
  const country = [
    {title:'Irlanda', slug:'ireland'},
    {title:'Canadá', slug:'canada'},
    {title:'Estados Unidos', slug:'usa'},
    {title:'Austrália', slug:'australia'},
    {title:'Nova Zelândia', slug:'newzeland'},
  ]

      useEffect(()=>{

        const token = sessionStorage.getItem('token');
        async function getUser(){

         await Api.get('/user' , { headers: {"Authorization" : `Bearer ${token}`} })
          .then((res) => {
          setUser((res.data))     
        })
        console.log(user)
      }

      getUser()
},[])





  return (
    <div>
        <h2>Olá {(user.name).split(' ')[0]}!</h2>
        <p>Bem vindo a ferramenta que vai te ajudara realizar seu sonho de intercambista.</p>
        <p>Agora vamos configurar o seu “Dashboard”.</p>
        <form >
          <label>
            <h5>Adicione uma photo sua.</h5>
            <input type="file" name="photo" />
          </label>
          <label>
            <h5>Qual pais você vai?</h5>
            <select name="country">
                {country.map((item)=>(
                  <option value={item.slug}>{item.title}</option>
                ))}
            </select>
          </label>
        </form>
    </div>
  )
}

export default CreateUserOptions
