import {useState,useEffect} from 'react'
import Api from './../../axios/Axios'

const CreateUserOptions = () => {

  const [user,setUser] = useState(null)
  const [userCountry, setUserCountry] = useState(null)
  const [userCityFromCountry, setUserCityFromCountry] = useState([])
  const country = [
    {title:'Irlanda', slug:'ireland', city:[{title:'Dublin'},{title:'Cork'},{title:'Galway'},{title:'Limerick'},{title:'Waterford'},]},
    {title:'Canadá', slug:'canada', city:[{title:'Quebec'},{title:'Ottawa'},{title:'Montreal'},{title:'Toronto'},{title:'Vancouver'},]},
    {title:'Estados Unidos', slug:'usa', city:[{title:'Quebec'},{title:'Ottawa'},{title:'Montreal'},{title:'Toronto'},{title:'Vancouver'},]},
    {title:'Austrália', slug:'australia', city:[{title:'New York'},{title:'Los Angeles'},{title:'Chicago'},{title:'Denver'},{title:'California'},]},
    {title:'Nova Zelândia', slug:'newzeland', city:[{title:'Wellington'},{title:'Christchurch'},{title:'Queenstown'},]},
  ]


  function userCity(value){
      country.map((item)=>{
        if(item.slug == value){
          console.log(item)
          setUserCityFromCountry(item.city)
        }
      })
      console.log(userCityFromCountry)
      console.log(value)
  }
      useEffect(()=>{

        const token = sessionStorage.getItem('token');
        async function getUser(){

         await Api.get('/user' , { headers: {"Authorization" : `Bearer ${token}`} })
          .then((res) => {
          setUser((res.data))     
        })
        console.log(user)
      }

      getUser()
},[])





  return (
    <div>{user !== null ? <>

        <h2>Olá {(user.name).split(' ')[0]}!</h2>
        <p>Bem vindo a ferramenta que vai te ajudara realizar seu sonho de intercambista.</p>
        <p>Agora vamos configurar o seu “Dashboard”.</p>
        <form >
          <label>
            <h5>Adicione uma photo sua.</h5>
            <input type="file" name="photo" />
          </label>
          <label>
            <h5>Qual pais você vai?</h5>
            <select onChange={(e)=>{userCity(e.target.value)}} name="country"> 
                 <option value={null}>Selecione um Pais</option>

                {country.map((item)=>(
                  <option key={item.slug} value={item.slug}>{item.title}</option>
                ))}
            </select>
          </label>
          <label>
            <h5>Qual a cidade?</h5>
            <select name="city">
              <option value={null}>Selecione uma cidade</option>
                {userCityFromCountry.map((item)=>(
                  <option key={item.title} value={item.title}>{item.title}</option>
                ))}
            </select>
          </label>
          <label>
            <h5>Quanto R$ você pretende gastar?</h5>
            <input type="number" placeholder='3000'/>
          </label>
        :</form>
        </>
        :''}
    </div>
  )
}

export default CreateUserOptions
