import {useEffect, useState} from 'react'
import './ListCategory.css'
import Api from '../../axios/Axios'

const ListCategory = () => {


    const [articles, setArticles] = useState(undefined);
    const [categories, setCategories] = useState(undefined);

    const [user,setUser] = useState([])
    let token = ''

    const local = localStorage.getItem('token');
    const session = sessionStorage.getItem('token');
    if(!session){
        if(!local){
            window.location.href = '/login'
        } else{
          token = local
            sessionStorage.setItem('token',local)
        }
    } else{
      token = session




    }

    useEffect(()=>{
        getOptions()
        async function getOptions(){
          const config = {
            headers: { Authorization: `Bearer ${token}` }
        };
          await Api.get('/user/useroptions', config).then((data)=>{
            if(!data.data[0]){
              window.location.href = '/createuseroptions'
            } else{
              setUser(data.data[0])

            }
          })
        }
      },[])




      useEffect(()=>{

            async function getCategories(){
                const config = {
                    headers: { Authorization: `Bearer ${token}` }}
                await Api.get('/blog/categories' , config).then((data)=>{
                    setCategories(data.data)
                    console.log(data)
                })
            }
            getCategories()

    },[user])



    useEffect(()=>{
        if(user.country !== undefined){

            async function getArticles(){
                await Api.get(`/blog/articles?country=${user.country}`).then((data)=>{
                    setArticles(data.data)
                    console.log(data)
                })
            }
           // getArticles()
        }
    },[user])



  return (
    <div className='blog-list-category'>

        {categories !== undefined? 

            <ul>
                {categories.map((item)=>(
                  <a key={item.id} href={`/blog?category=${item.slug}`}>  <li >{item.title}</li></a>
                ))}

            </ul>

        :''}
        {articles!== undefined ? 

        articles.map((item)=>(
            <article>
                <div className="article-poster"></div>
                <h2 className="article-title">{item.title}</h2>
                <h5 className="arcitle-category"></h5>
                <p className="article-description"></p>
            </article>

        ))
        :''}
    </div>
  )
}

export default ListCategory
